pages/order-confirmation:
{{#partial "head"}}
{{{ checkout.checkout_head }}}
{{{ stylesheet '/assets/css/optimized-checkout.css' }}}
{{ getFontsCollection }}

<script type="text/javascript">
    window.language = {{{langJson 'optimized_checkout'}}};
</script>

{{/partial}}

{{#partial "page"}}
<header class="checkoutHeader optimizedCheckout-header">
    <div class="checkoutHeader-content">
        <h1 class="is-srOnly">{{lang 'checkout.title'}}</h1>
        <h2 class="checkoutHeader-heading">
            <a class="checkoutHeader-link" href="{{urls.home}}">
                {{#if checkout.header_image}}
                    <img alt="{{settings.store_logo.title}}" class="checkoutHeader-logo" id="logoImage" src="{{ checkout.header_image }}"/>
                {{ else }}
                    <span class="header-logo-text">{{settings.store_logo.title}}</span>
                {{/if}}
            </a>
        </h2>
    </div>
</header>
<script type="text/javascript"> 
    var sa_order_name = '%%GLOBAL_CustomerName%%';
    sa_order_name = $.trim(sa_order_name.replace(/,/g, '').replace(/!/g, ''));
    var sa_values = { 'site':24837, 'orderid':'%%GLOBAL_OrderId%%', 'name':sa_order_name, 'email':sa_order_email, 'forcecomments':1 };  function saLoadScript(src) { var js = window.document.createElement("script"); js.src = src; js.type = "text/javascript"; document.getElementsByTagName("head")[0].appendChild(js); } var d = new Date(); if (d.getTime() - 172800000 > 1445569126000) saLoadScript("//www.shopperapproved.com/thankyou/rate/24837.js"); else saLoadScript("//direct.shopperapproved.com/thankyou/rate/24837.js?d=" + d.getTime());
</script>
{{{ checkout.order_confirmation_content }}}
{{{footer.scrips}}}
{{/partial}}
{{> layout/empty}}
